import { useState } from 'react'
import styles from '../Form/Form.module.css'
import validations from './validations'

export default function Form(props){
    const [userData, setUserData]= useState({
        email:'',
        password:'',
    })

    const [errors,setErrors]=useState({
        email:'',
        password:'',
    })

    const handleChange=(event)=>{
        setUserData({
            ...userData,
            [event.target.name]: event.target.value
        })

        setErrors(
            validations({
                ...userData,
                [event.target.name]: event.target.value
            } ,userData, errors)
        )
    }

    const handleSubmit=(e)=>{
        e.preventDefault()
        props.login(userData)
    }

    return(
        <form action="" className={styles.form}>
        <main className={styles.main}>Login</main>

        <label htmlFor="email">Email</label>
        <input required type="text" name="email" value={userData.email} onChange={handleChange}/>
        {errors.email && <p className={styles.warning}>{errors.email}</p>}

        <label htmlFor="password">Password</label>
        <input required type="text" name="password" value={userData.password} onChange={handleChange}/>
        {errors.password && <p className={styles.warning}>{errors.password}</p>}

        <button type="submit">Submit</button>

    </form>
    )
}
