import {ADD_FAV, REMOVE_FAV} from './action-type'

const initialState={
    myFavorites:[]
}

const rootReducer=(state=initialState, action)=>{
    switch (action.type) {
        case ADD_FAV:
            return {...state.myFavorites, myFavorites: [...state.myFavorites, action.payload],};
        
        case REMOVE_FAV:
            return {...state, myFavorites:state.filter((e)=>e.id!==Number(action.payload))};
    
        default:
            return state;
    }
}
