import axios from "axios"
import { useState } from "react";
import {useParams} from 'react-router-dom';
import { useEffect } from "react";


export default function Detail(props){

    const {id}= useParams();
    const {character, setCharacter}= useState({});

useEffect(() => {
    //     axios(`https://rickandmortyapi.com/api/character/${id}`).then(({ data }) => {
    //     if (data.name) {
    //         setCharacter(data);
    //         } else {
    //         window.alert('No hay personajes con ese ID');
    //         }
    //     });
    //     return setCharacter({});
    // }, [id]);

    (async () => {
        try {
            const data = await axios(
            `https://rickandmortyapi.com/api/character/${id}`
            );
            if (data) {
                setCharacter(data);
            } else {
                window.alert("No hay personajes con ese ID");
            }
        } catch (error) {
            window.alert(error.message);
        }
    })();
    return setCharacter({});
    }, [id]);

    return(
    <div> 
        {character.name && (
        <div>
            {character.name && <h1>Name: {character.name}</h1>}
            {character.image && (
            <img src={character.image} alt={character.name} />)}
            {character.status && <h2>Status: {character.status}</h2>}
            {character.species && <h2>Specie: {character.species}</h2>}
            {character.gender && <h2>Gender: {character.gender}</h2>}
            {character.origin && <h2>Origin: {character.origin.name}</h2>}
        </div>
        )}
    </div>
    )
}
