import data from "../../data";
import styles from '../Card/Card.module.css'
import {Link} from 'react-router-dom';
import {setStatus} from 'react';
import { addFav, removeFav } from '../Redux/action-type';
import {connect} from 'react-redux'
import { useState } from "react";



function Card(props) {
   const{removeFav, addFav, myFavorites,id,image,name,species,gender,origin,onClose,}= props;
   const [isFav, setIsFav]= useState(false)

   const handleFavorite = () => {
      if (isFav) {
        dispatch(removeFav(id));
        setIsFav(false);
      } else {
        const data = {
          id,
          image,
          name,
          species,
          origin,
          gender
        }
        dispatch(addFav(data));
        setIsFav(true);
      }
    };

   return (
         <div id='card' className={styles.card}>
            <button id= 'botonDelete' className= {styles.button} onClick={()=>{props.onClose(props.id)}}>X</button>
            {
            isFav ? (<button className={styles.buttonFav}>‚ù§Ô∏è</button>) 
            : (<button onClick={handleFavorite}>ü§ç</button>)
            }
               <img className= {styles.images} src={props.image} alt={props.name} /> 
               <Link to={`/detail/${props.id}`}>
               <div className={styles.charName}><h2>{props.name}</h2></div>
               </Link>

               <div className={styles.cardInfo}>
               <h5>Status: {props.status}</h5>
               <h5>Species: {props.species}</h5>
               <h5>Gender: {props.gender}</h5>
               <h5>Origin: {props.origin}</h5>
            </div>
            <div>
            </div>

         </div>
   );
}

const mapDispatchToProps=(dispatch)=>{
   return{
      addFav:(character)=>dispatch(addFav(character)),
      removeFav:(id)=>dispatch(removeFav(id))
   }
}

export default connect(mapDispatchToProps)(Card)
