import styles from '../src/App.css'
import axios from 'axios';
import { useState } from 'react';
import Nav from './components/Nav/Nav';
import Cards from './components/Cards/Cards.jsx';
import {Routes, Route} from 'react-router-dom';
import About from './components/About/About';
import Detail from './components/Detail/Detail'



function App() {
   const [characters, setCharacters]= useState([])
   const [id, setId] = useState('');

   function onSearch(id) {
   axios.get(`https://rickandmortyapi.com/api/character/${id}`)
   .then(({data}) => {
         if (!characters.some((char) => char.id === data.id)){
         setCharacters((oldChars) => [...oldChars, data])}

   })
   .catch((error) => {
   window.alert('¡No hay personajes con este ID!')
   });
   }

   const onClose=(id)=>{
      const filteredCharacters = characters.filter((character) => character.id !== parseInt(id));
      setCharacters(filteredCharacters);
   }

   const randomChar=()=>{
      let randomId= Math.floor(Math.random() * 826)
      axios.get(`https://rickandmortyapi.com/api/character/${randomId}`)
   .then(({data}) => {
         if (!characters.some((char) => char.id === data.id)){
         setCharacters((oldChars) => [...oldChars, data])}

   })
   }

   return (
      <div id='app' className={styles.background}>
         <Nav search={onSearch} random={randomChar}/>
         <Routes>
            <Route path='/home' element={<Cards characters={characters} onClose={onClose} id={id}/>}/>
            <Route path='/about' element={<About />} />
            <Route path='/detail/:id' element={<Detail />} />
            <Route path='*' element={<Error/>} />
         </Routes>

      </div>
   );
}

export default App;
